function myPopup(myURL, title, myWidth, myHeight) {
    var left = (screen.width - myWidth) / 2;
    var top = (screen.height - myHeight) / 4;
    var myWindow = window.open(myURL, title, 'toolbar=no, location=no, directories=no, status=no, menubar=no, scrollbars=no, resizable=no, copyhistory=no, width=' + myWidth + ', height=' + myHeight + ', top=' + top + ', left=' + left);
 }

 function popupDetails(index) {
    let length = document.getElementsByClassName("details-container").length;
    let popup = document.getElementsByClassName("details-container")[length-index];
    popup.style.display = "block";
  }

function closePopup(index) {
  let length = document.getElementsByClassName("details-container").length;
  let popup = document.getElementsByClassName("details-container")[length-index];
  popup.style.display = "none";
}

window.addEventListener("load", function() {
   const form = document.getElementById('contact-me');
   const response = document.getElementById('form-message-success');
   form.addEventListener("submit", function(e) {
     e.preventDefault();
     const data = new FormData(form);
     const action = e.target.action;
     fetch(action, {
       method: 'POST',
       body: data,
     })
     .then(() => {
       response.style.display = "block";
     })
   });
 });
 